import{_ as e,c as r,o as t,ag as i}from"./chunks/framework.Ii2o3pk_.js";const u=JSON.parse('{"title":"RAG - 检索增强生成","description":"","frontmatter":{},"headers":[],"relativePath":"ai/generative/rag.md","filePath":"ai/generative/rag.md"}'),l={name:"ai/generative/rag.md"};function o(n,a,d,c,h,s){return t(),r("div",null,a[0]||(a[0]=[i('<h1 id="rag-检索增强生成" tabindex="-1">RAG - 检索增强生成 <a class="header-anchor" href="#rag-检索增强生成" aria-label="Permalink to &quot;RAG - 检索增强生成&quot;">​</a></h1><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><h3 id="什么是-rag" tabindex="-1">什么是 RAG <a class="header-anchor" href="#什么是-rag" aria-label="Permalink to &quot;什么是 RAG&quot;">​</a></h3><p>RAG（Retrieval-Augmented Generation） 即<code>检索增强生成</code> ，将信息检索与文本生成相结合，是一种结合向量检索与大语言模型生成能力的框架。核心理念是先检索，再生成。</p><blockquote><p>RAG 不是模型本身，而是一种“让大模型拥有私有知识的能力架构”，属于生成式 AI 应用层最关键的落地路径之一。</p></blockquote><h3 id="核心价值" tabindex="-1">核心价值 <a class="header-anchor" href="#核心价值" aria-label="Permalink to &quot;核心价值&quot;">​</a></h3><p>RAG 的核心价值在于解决了 LLM 的三大痛点</p><ol><li>知识固化：预训练数据无法实时更新</li><li>幻觉问题：生成内容缺乏事实依据</li><li>领域局限：难以直接处理专业领域问题</li></ol><h3 id="标准流程结构" tabindex="-1">标准流程结构 <a class="header-anchor" href="#标准流程结构" aria-label="Permalink to &quot;标准流程结构&quot;">​</a></h3><ol><li>读取用户输入提示词</li><li>将问题转化为向量</li><li>在向量数据库中找相关文档</li><li>对数据进行截取，清洗等</li><li>基于原始提问 + 检索内容生成答案</li></ol>',10)]))}const p=e(l,[["render",o]]);export{u as __pageData,p as default};
